import mathclass HinhTamGiac:    # thuộc tính    def __init__(self, a, b, c):        # constructor --- rồi sẽ có decontructur để xóa        self.a = a  # giá trị tham số a truyền vào cho thuộc tính self.a        self.b = b        self.c = c    # phương thức    def chu_vi(self):        return self.a + self.b + self.c    def TamGiacVuong(self):        pass  # nếu chưa làm gì thì cho pass vào để khỏi báo lỗi    def TinhDienTich(self):        self.p = (self.a + self.b + self.c) / 2  # cách này ngu vcl :)        nuachuvi = self.chu_vi() / 2  # đã tính chu vi ở trên nên dùng lại công thức heror        return (math.sqrt(nuachuvi * (nuachuvi - self.a) * (nuachuvi - self.b) * (nuachuvi - self.c)))    def in_thong_tin(self):        print("*" * 20)        print("Toi la mot tam giac")        print("Canh tam giac: a=", self.a, " b=", self.b, " c=", self.c)        print("Chu vi:", self.chu_vi(), "\t - \tDien tich:", self.TinhDienTich(), "\n")if __name__ == "__main__":    htg_1 = HinhTamGiac(3, 4, 5)    htg_2 = HinhTamGiac(2, 5, 4)    htg_3 = HinhTamGiac(10, 8, 9)    lst = [htg_1, htg_2, htg_3]  # tạo 1 list chưa các hình tam giác    lst.append((HinhTamGiac(5, 3, 4)))  # add thêm vào một hình tam giác vào list , hiện giờ lst có 4 phần tử    for tam_giac in lst:  # muốn duyệt qua tất cả phần tử của list phải chạy vòng for        tam_giac.in_thong_tin()