import mathclass HinhTamGiac:    def __init__(self, a, b, c):        self.a = a  # giá trị tham số a truyền vào cho thuộc tính self.a        self.b = b        self.c = c    def chu_vi(self):        return self.a + self.b + self.c    def TamGiacVuong(self):        pass  # nếu chưa làm gì thì cho pass vào để khỏi báo lỗi    @property    def TinhDienTich(self):        self.p = (self.a + self.b + self.c) / 2  # cách này ngu vcl :)        nuachuvi = self.chu_vi() / 2  # đã tính chu vi ở trên nên dùng lại công thức heror        return (math.sqrt(nuachuvi * (nuachuvi - self.a) * (nuachuvi - self.b) * (nuachuvi - self.c)))if __name__ == "__main__":    htg_1 = HinhTamGiac(3, 4, 5)    htg_2 = HinhTamGiac(2, 5, 4)    htg_3 = HinhTamGiac(10, 8, 9)    print("chu vi hinh tam gia 1:", htg_1.chu_vi())    print("chu vi hinh tam gia 2:", htg_2.chu_vi())    print("chu vi hinh tam gia 3:", htg_3.chu_vi())    print("___________________________")    print("dien tich hinh tam gia 1:", htg_1.TinhDienTich)    print("dien tich hinh tam gia 2:", htg_2.TinhDienTich)    print("dien tich hinh tam gia 3:", htg_3.TinhDienTich)